import { useState } from "react";

function Guadagni(props) {
  console.log(props.dataHero);
  const hero = props.dataHero;
  let percentage = 1;

  const [heroInfo, setHeroInfo] = useState(
    [
      {
        value: daily,
        name: "THC al giorno",
        usd: dailyUSD,
      },
      {
        value: guadagnoPotenziale,
        name: "Guadagno Potenziale",
        usd: guadagnoUSD,
      },
      {
        value: profittoPotenziale,
        name: "Profitto Potenziale",
        usd: profittoUSD,
      },
    ]
  )


  function percentageVar(perc) {
    switch(perc) {
      case 25:
        percentage = 0.25;
        break;
      case 50:
        percentage = 0.5;
        break;
      case 75:
        percentage = 0.75;
        break;
      default: percentage = 1;
    }
  }

  const battaglieRimanenti =
    hero.heroRanking.totalBattleCapTHC - hero.heroRanking.battleCapTHC;

  const guadagnoPotenziale =
    (hero.thcBonus + 6) * battaglieRimanenti * percentage;
  const profittoPotenziale =
    (guadagnoPotenziale - hero.sale.price.value / 100000000) * percentage;
  const daily = (hero.thcBonus + 6) * hero.dailyTHCBattleConfig * percentage;

  const guadagnoUSD = (guadagnoPotenziale * props.prezzoTHC).toFixed(2);
  const profittoUSD = (profittoPotenziale * props.prezzoTHC).toFixed(2);
  const dailyUSD = (
    (hero.thcBonus + 6) *
    hero.dailyTHCBattleConfig *
    props.prezzoTHC
  ).toFixed(2);

  return (
    <div>
      <div class="mainLayout flexSpace marginTop10 maxWidth350">
        <button onClick={() => percentageVar(25)}>25%</button>
        <button onClick={() => percentageVar(50)}>50%</button>
        <button onClick={() => percentageVar(75)}>75%</button>
        <button onClick={() => percentageVar()}>100%</button>
      </div>
      {hero ? (
        info.map((data) => {
          console.log(data);
          return (
            <div class="mainLayout flexSpace marginTop10 maxWidth350">
              <div>
                <h3>{data.name}</h3>
              </div>
              <div class="resultLabel">
                <div class="guadagnoLabel">
                  <h3 id="profittoPotenziale">{data.value} THC</h3>
                  <div class="usd" id="profittoPotenzialeUSD">
                    {data.usd} USD
                  </div>
                </div>
                <div
                  style={{ backgroundImage: "url(" + props.coin + ")" }}
                  class="thc"
                ></div>
              </div>
            </div>
          );
        })
      ) : (
        <div>"NIENTE"</div>
      )}
    </div>
  );
}

export default Guadagni;
