function InfoPersonaggio(props) {
  console.log(props.dataHero);
  const hero = props.dataHero;

  let info = [];
  if(hero) {
      info = [
        {
            value: hero.sale.price.value / 100000000,
            name: "Costo"
        },
        {
            value: hero.heroRanking.totalBattleCapTHC,
            name: "Battagle Totali"
        },
        {
            value: hero.heroRanking.battleCapTHC,
            name: "Battaglie Bruciate"
        },
        {
            value: hero.dailyTHCBattleConfig,
            name: "Daily Battles"
        },
        {
            value: hero.thcBonus,
            name: "Bonus THC"
        }
      ]
  }
  return (
      <div>
          {
              hero ? 
              info.map((data) => {
                  console.log(data)
                return (
                  <div className="mainLayout flexSpace marginTop10 maxWidth350">
                    <div className="input">
                      <div>{data.value}</div>
                      <div className="inputLabel thc"></div>
                    </div>
                    <div className="label">
                      <h3>{data.name}</h3>
                    </div>
                  </div>
                )
            })
              : <div>"NIENTE"</div>
          }
      </div>

  );
}

export default InfoPersonaggio;
