function InfoPersonaggio(props) {
  console.log(props.dataHero);
  const hero = props.dataHero;

  let info = [];
  if (hero) {
    // se il personaggio è in vendita
    if (hero.sale) {
        if(hero.rentInfo) {
            // se il personaggio è in affitto
            info = [
                {
                  value: hero.rentInfo.cost.value / 100000000,
                  name: "Costo",
                  usd: ((hero.rentInfo.cost.value / 100000000) * props.prezzoTHC).toFixed(
                    2
                  ),
                },
                {
                  value: hero.rentInfo.rentBattles,
                  name: "Battagle Totali",
                },
                {
                  value: 0,
                  name: "Battaglie Bruciate",
                },
                {
                  value: hero.dailyTHCBattleConfig,
                  name: "Daily Battles",
                },
                {
                  value: hero.thcBonus,
                  name: "Bonus THC",
                  usd: (hero.thcBonus * props.prezzoTHC).toFixed(2),
                },
              ];
        } else {
            // Se il personaggio è in vendita
            info = [
                {
                  value: hero.sale.price.value / 100000000,
                  name: "Costo",
                  usd: ((hero.sale.price.value / 100000000) * props.prezzoTHC).toFixed(
                    2
                  ),
                },
                {
                  value: hero.heroRanking.totalBattleCapTHC,
                  name: "Battagle Totali",
                },
                {
                  value: hero.heroRanking.battleCapTHC,
                  name: "Battaglie Bruciate",
                },
                {
                  value: hero.dailyTHCBattleConfig,
                  name: "Daily Battles",
                },
                {
                  value: hero.thcBonus,
                  name: "Bonus THC",
                  usd: (hero.thcBonus * props.prezzoTHC).toFixed(2),
                },
              ];
        }

    } else {
      // se il personaggio è stato comprato
      if (hero.lastPrice) {
        info = [
          {
            value: hero.lastPrice / 100000000,
            name: "Costo",
            usd: (
              (hero.lastPrice / 100000000) *
              props.prezzoTHC
            ).toFixed(2),
          },
          {
            value: hero.heroRanking.totalBattleCapTHC,
            name: "Battagle Totali",
          },
          {
            value: hero.heroRanking.battleCapTHC,
            name: "Battaglie Bruciate",
          },
          {
            value: hero.dailyTHCBattleConfig,
            name: "Daily Battles",
          },
          {
            value: hero.thcBonus,
            name: "Bonus THC",
            usd: (hero.thcBonus * props.prezzoTHC).toFixed(2),
          },
        ];
      } else {
        // SE IL PERSONAGGIO è STATO TROVATO IN UNA BOX
        let rarity = 1000;
        switch (hero.heroInfo.rarity) {
          case 0:
            rarity = 1000;
            break;
          case 1:
            rarity = 2200;
            break;
          default:
            rarity = 0;
        }
        info = [
          {
            value: rarity,
            name: "Costo",
            usd: (rarity * props.prezzoTHC).toFixed(2),
          },
          {
            value: hero.heroRanking.totalBattleCapTHC,
            name: "Battagle Totali",
          },
          {
            value: hero.heroRanking.battleCapTHC,
            name: "Battaglie Bruciate",
          },
          {
            value: hero.dailyTHCBattleConfig,
            name: "Daily Battles",
          },
          {
            value: hero.thcBonus,
            name: "Bonus THC",
            usd: (hero.thcBonus * props.prezzoTHC).toFixed(2),
          },
        ];
      }
    }
  }
  return (
    <div>
      {hero ? (
        info.map((data, index) => {
          console.log(data);
          return (
            <div
              key={index}
              className="mainLayout flexSpace marginTop10 maxWidth350"
            >
              <div>
                <h3>{data.name}</h3>
              </div>
              <div className="resultLabel">
                <div className="guadagnoLabel">
                  <h3 id="profittoPotenziale">
                    {data.value} {data.usd && " THC"}
                  </h3>
                  {data.usd && (
                    <div className="usd" id="profittoPotenzialeUSD">
                      {data.usd} USD
                    </div>
                  )}
                </div>
                {data.usd && (
                  <div
                    style={{ backgroundImage: "url(" + props.coin + ")" }}
                    className="thc"
                  ></div>
                )}
              </div>
            </div>
          );
        })
      ) : (
        <div>"NIENTE"</div>
      )}
    </div>
  );
}

export default InfoPersonaggio;
