import { useEffect, useState } from "react";

function Guadagni(props) {
  console.log(props.dataHero);
  const hero = props.dataHero;
  let percentage = 1;

  const [colore1, setColore1] = useState();
  const [colore2, setColore2] = useState();
  const [colore3, setColore3] = useState();
  const [colore4, setColore4] = useState();
  const activeColor = "#2290FF";
  const standardColor = "#fbba16"



  const [heroInfo, setHeroInfo] = useState([]);

  useEffect(() => {
    percentageVar()
  }, [])

  function percentageVar(perc) {
    switch (perc) {
      case 25:
        percentage = 0.25;
        setColore1(activeColor)
        setColore2(standardColor)
        setColore3(standardColor)
        setColore4(standardColor)
        setHero();
        break;
      case 50:
        percentage = 0.5;
        setColore1(standardColor)
        setColore2(activeColor)
        setColore3(standardColor)
        setColore4(standardColor)
        setHero();
        break;
      case 75:
        percentage = 0.75;
        setColore1(standardColor)
        setColore2(standardColor)
        setColore3(activeColor)
        setColore4(standardColor)
        setHero();
        break;
      default:
        percentage = 1;
        setColore1(standardColor)
        setColore2(standardColor)
        setColore3(standardColor)
        setColore4(activeColor)
        setHero();
    }
  }

  function setHero() {
    const battaglieRimanenti =
      hero.heroRanking.totalBattleCapTHC - hero.heroRanking.battleCapTHC;

    const guadagnoPotenziale =
      (hero.thcBonus + 6) * battaglieRimanenti * percentage;
    const profittoPotenziale =
      (guadagnoPotenziale - hero.sale.price.value / 100000000) * percentage;
    const daily = (hero.thcBonus + 6) * hero.dailyTHCBattleConfig * percentage;

    const guadagnoUSD = (guadagnoPotenziale * props.prezzoTHC).toFixed(2);
    const profittoUSD = (profittoPotenziale * props.prezzoTHC).toFixed(2);
    const dailyUSD = (daily * props.prezzoTHC).toFixed(2);
    setHeroInfo([
      {
        value: daily,
        name: "THC al giorno",
        usd: dailyUSD,
      },
      {
        value: guadagnoPotenziale,
        name: "Guadagno",
        usd: guadagnoUSD,
      },
      {
        value: profittoPotenziale,
        name: "Profitto",
        usd: profittoUSD,
      },
    ]);
  }

  return (
    <div>
      <div className="mainLayout flexSpace marginTop10 maxWidth350 column">
        <h3>Percentuale Vittorie</h3>
        <div style={{display: "flex"}} className="flexSpace">
          <button style={{backgroundColor: colore1}} className="buttonOne " onClick={() => percentageVar(25)}>25%</button>
          <button style={{backgroundColor: colore2}} className="buttonOne " onClick={() => percentageVar(50)}>50%</button>
          <button style={{backgroundColor: colore3}} className="buttonOne " onClick={() => percentageVar(75)}>75%</button>
          <button style={{backgroundColor: colore4}} className="buttonOne " onClick={() => percentageVar()}>100%</button>
        </div>
      </div>
      {heroInfo ? (
        heroInfo.map((data) => {
          console.log(data);
          return (
            <div className="mainLayout flexSpace marginTop10 maxWidth350">
              <div>
                <h3>{data.name}</h3>
              </div>
              <div className="resultLabel">
                <div className="guadagnoLabel">
                  <h3 id="profittoPotenziale">{data.value} THC</h3>
                  <div className="usd" id="profittoPotenzialeUSD">
                    {data.usd} USD
                  </div>
                </div>
                <div
                  style={{ backgroundImage: "url(" + props.coin + ")" }}
                  class="thc"
                ></div>
              </div>
            </div>
          );
        })
      ) : (
        <div>"NIENTE"</div>
      )}
    </div>
  );
}

export default Guadagni;
