function InfoPersonaggio(props) {
  console.log(props.dataHero);
  const hero = props.dataHero;


  let info = [];
  if(hero) {
      info = [
        {
            value: hero.sale.price.value / 100000000,
            name: "Costo",
            usd: ((hero.sale.price.value / 100000000) * props.prezzoTHC).toFixed(2)
        },
        {
            value: hero.heroRanking.totalBattleCapTHC,
            name: "Battagle Totali"
        },
        {
            value: hero.heroRanking.battleCapTHC,
            name: "Battaglie Bruciate"
        },
        {
            value: hero.dailyTHCBattleConfig,
            name: "Daily Battles"
        },
        {
            value: hero.thcBonus,
            name: "Bonus THC",
            usd: (hero.thcBonus * props.prezzoTHC).toFixed(2)
        }
      ]
  }
  return (
      <div className="calcolatore">
          {
              hero ? 
              info.map((data) => {
                  console.log(data)
                return (
                    <div class="mainLayout flexSpace marginTop10 maxWidth350">
                    <div><h3>{data.name}</h3></div>
                    <div class="resultLabel">
                      <div class="guadagnoLabel">
                        <h3 id="profittoPotenziale">{data.value} {
                        data.usd && " THC"
                        }</h3>
                        {
                            data.usd &&
                            <div class="usd" id="profittoPotenzialeUSD">{data.usd} USD</div>
                        }
                      </div>
                      {
                          data.usd && <div style={{backgroundImage: "url("+props.coin+")"}} class="thc"></div>
                      }
                    </div>
                  </div>
                )
            })
              : <div>"NIENTE"</div>
          }
      </div>

  );
}

export default InfoPersonaggio;
